mapper.update:
update article
set visit = visit + 1
where id = #{articleId}

update article
set is_private = !is_private
where id = #{articleId}
  and user_id = #{userId}

update article
set title      = #{title},
    clazz_id=#{clazzId},
    summary=#{summary},
    update_time=#{updateTime}
where user_id = #{userId}
  and id = #{id}

mapper.select:
select count(id) as number, sum(`like`) as `like`, sum(visit) as visit
from article
where user_id = #{userId}

    and clazz_id = #{clazzId}

group by user_id

select userId
from article
where id = #{aritlceId}
